import './Profile.css';
import { Navbar } from '../../components';
import { useToken } from '../../hooks';
import { Navigate } from 'react-router-dom';
import { useState } from 'react';
// try usestate
async function getTrips(uname) {
    let res = await fetch('/getusertrips', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({username: uname})
    });

    if (res.status !== 200) {
        return null;
    }

    let ret = res.json();
    console.log(ret);
    return ret;
}

export default async function Profile() {
    const { token, setToken } = useToken();
    const [ trips, setTrips ] = useState(<li></li>);

    if (!token) {
        alert("You need to log in first.");
        return(
            <Navigate to='/login' />
        );
    }

    let username = token[0]?.user;
    setTrips(await getTrips(username));


    return (
        <main className='profile-page'>
            <header className='navbar'>
                <Navbar />
            </header>
            <div className='profile body'>
                <div className='profile-username'>
                    {username}
                </div>
                <div className='profile-trips'>
                    <ul className='trips-ul'>
                        {trips.map((trip) => { return <li><a href={"/trip/" + trip.id}>{trip.name}</a></li> })}
                    </ul>
                </div>
            </div>
        </main>
    );
}
