export default class Token {
    constructor(token_name, token_changed_listener=undefined) {
        this.name = token_name;
        if (token_changed_listener && typeof token_changed_listener.notify === 'function') {
            this.listeners = [];
            this.listeners.push(token_changed_listener);
        }
    }

    getToken() {
        return JSON.parse(sessionStorage.getItem(this.name))?.token;
    }

    saveToken(token) {
        sessionStorage.setItem(this.name, JSON.stringify(token));
        this.listeners.forEach(function (listener) {listener.notify()})
    }
}
