import { useState } from 'react';

export default function useToken() {
    // gets the token from storage
    const getToken = () => {
        const tokenString = sessionStorage.getItem('token');
        const userToken = JSON.parse(tokenString);
        return userToken?.token // if userToken.token doesn't exists return undefined
    };

    const [token, setToken] = useState(getToken());
    console.log("inside useToken, token: " + token);

    // saves the token in storage
    const saveToken = userToken => {
        sessionStorage.setItem('token', JSON.stringify(userToken));
        setToken(userToken.token);
        console.log(userToken.token)
    };

    // return {secure_func_to_change_token, token}
    return {
        setToken: saveToken,
        token
    }
}
