import './DisplayTrip.css';
import PropTypes from 'prop-types';
import DisplayDay from '../displayday/DisplayDay';
import { useState } from 'react';

// {JSON.stringify(trip.days[0].activities[0])}
export default function DisplayTrip({ trip, setEditedTrip }) {
    const [ displayed, setDisplayed ] = useState({ title:'', link:'' });

    return(
    <div className="displaytrip-container">
        <div className='activities-display'>
            {trip?.days.map((d, i) => { return <DisplayDay day={d} index={i} key={i} setEditedTrip={setEditedTrip} notifyPressed={(_title, _link) => setDisplayed({ title:_title, link:_link })} /> })}
        </div>
        <div className='displayitem'>
            <iframe className='googlemaps' title='info' src={displayed.link + "&output=embed"}></iframe>
            <iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3380.2344762701096!2d34.78227068501786!3d32.08994798118484!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A0x1144856d5a5b3f9b!2zMzLCsDA1JzIzLjgiTiAzNMKwNDYnNDguMyJF!5e0!3m2!1siw!2sil!4v1655301507968!5m2!1siw!2sil" width="600" height="450" allowfullscreen="" loading="lazy" referrerpolicy="no-referrer-when-downgrade"></iframe>
        </div>
    </div>
    )
}

DisplayTrip.propTypes = {
    trip: PropTypes.object.isRequired,
    setEditedTrip: PropTypes.func
}
